#include "abs_gyro_drive.h"
#include "lib/abs_drive.h"
#include "lib/abs_initialize.h"

//========================================
// Main program
//========================================

task main()
{
	initialize();
	switch(mission_number)
	{
	case 1:
		abs_drive(FORWARD, E_IR_DETECT, 10, 60, true);
		PlayTone(20,200);
		break;

	case 2:
		abs_turn(CLOCKWISE, SWING, 120, 60);
		break;

	case 3:
		abs_drive(BACKWARD, E_TIME, 30000, 60, true);
		break;

	case 4:
		abs_turn(COUNTERCLOCKWISE, POINT, 90, 60);
		abs_turn(CLOCKWISE, POINT, 90, 60);
		abs_turn(COUNTERCLOCKWISE, POINT, 90, 60);
		abs_turn(CLOCKWISE, POINT, 90, 60);
		abs_turn(COUNTERCLOCKWISE, POINT, 90, 60);
		abs_turn(CLOCKWISE, POINT, 90, 60);
		break;
	}
}
